  {
    super(props);
    this.state={k: 0};
    this.handleChange = (e) => this.setState({k: e.target.value});
  }
  state ={k: 0};
  render() {
    return (
      <div>
        <h2>Kalkulator n-go wyrazu ciągu Fibonacciego</h2>
        <input type="number" value={this.state.k} onChange={this.handleChange}/><br/>
        {printFib(this.state.k)}
    </div>
    );
  }
}

