import React,{ Component } from "react";
import axios from 'axios';
//const { useState } =React;


class Fib extends Component {
  constructor(props)
  {
    super(props);
    this.state={k: 0,
                values:{},
                indexes:[]};
    this.handleChange = (e) => this.setState({k: e.target.value});
  }
  componentDidMount(){
    this.fetchValues();
    this.fetchIndexes();
  }
  state ={k: 0};
  async fetchValues(){
    const values= await axios.get('/api/values/current');
    this.setState({ values: values.data });
  }
  handleSubmit = async (e) =>
  {
    e.preventDefault();
    await axios.post('/api/values', {
      index: this.state.k,
    });
  }
  renderValues()
  {
    const entries=[];
    for(let i in this.state.values)
    {
      entries.push(
        <li>Element {i} &emsp; Wynik: {this.state.values[i]}</li>
      )
    }
  }
  render() {
    return (
      <div>
        <h2>Kalkulator n-go wyrazu ciÄ…gu Fibonacciego</h2>
        <div id="fibo">
          <input type="text" value={this.state.k} onChange={this.handleChange}/><br/>
          <h3>
          {printFib(this.state.k)}
          </h3>
          <button onClick={this.handleSubmit}>Zapisz</button>
          <div id="saved">
            <h3>Ostatnio zapisane</h3>
            { renderValues() }
          </div>
        </div>
      </div>
    );
  }
}

