function printFib(k)
{
  var fib_val=calculateFib(k);
  return(
    <div>
      Wynik: {fib_val}
    </div>
  );
}
function calculateFib(k)
{
  /*
  //rozwiazanie rekursywne
  if(k==0)
  {
    return 0;
  }
  else if(k==1)
  {
    return 1;
  }
  else if(k<0)
  {
    return;
  }
  else
  {
    return calculateFib(k-1)+calculateFib(k-2);
  }
  */
  //rozwiazanie w petli
  if(k==0)
  {
    return 0;
  }
  if(k<0)
  {
    return;
  }
  var fib1=0;
  var fib2=1;
  var fibo;
  for(var i=1;i<=k;i++)
  {
    fibo=fib1+fib2;
    fib1=fib2;
    fib2=fibo;
  }
  return fib1;
}

export default Fib;
